import React from 'react';

export default function Dashboard() {
    // Dados fictÃ­cios para o dashboard
    const analyticsData = {
        totalFans: 12458,
        engagementRate: 4.2,
        topPlayers: [
            { name: "FalleN", matchPercent: 92 },
            { name: "arT", matchPercent: 88 },
            { name: "KSCERATO", matchPercent: 76 }
        ],
        popularGames: ["CS2", "Valorant", "LoL"]
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white p-8">
            {/* CabeÃ§alho */}
            <div className="flex justify-between items-center mb-8 border-b border-gray-700 pb-4">
                <h1 className="text-3xl font-bold text-red-500">
                    <img src="/images/furia-logo.png" className="h-10 inline mr-3" alt="FURIA" />
                    Dashboard de Marketing
                </h1>
                <span className="bg-gray-800 px-4 py-2 rounded-lg text-sm">Acesso Restrito</span>
            </div>

            {/* MÃ©tricas Principais */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                <MetricCard
                    title="Total de FÃ£s"
                    value={analyticsData.totalFans.toLocaleString()}
                    icon="ðŸ‘¥"
                />
                <MetricCard
                    title="Taxa de Engajamento"
                    value={`${analyticsData.engagementRate}%`}
                    icon="ðŸ“ˆ"
                />
                <MetricCard
                    title="Jogo Principal"
                    value={analyticsData.popularGames[0]}
                    icon="ðŸŽ®"
                />
            </div>

            {/* Match com Jogadores */}
            <div className="bg-gray-800 rounded-xl p-6 mb-8">
                <h2 className="text-xl font-bold mb-4 text-yellow-400">ðŸ”— Match com Jogadores</h2>
                <div className="space-y-4">
                    {analyticsData.topPlayers.map(player => (
                        <div key={player.name} className="flex items-center">
                            <div className="w-32 font-medium">{player.name}</div>
                            <div className="flex-1 bg-gray-700 rounded-full h-4">
                                <div
                                    className="bg-gradient-to-r from-red-500 to-yellow-400 h-4 rounded-full"
                                    style={{ width: `${player.matchPercent}%` }}
                                />
                            </div>
                            <div className="w-16 text-right">{player.matchPercent}%</div>
                        </div>
                    ))}
                </div>
            </div>

            {/* GrÃ¡fico FictÃ­cio */}
            <div className="bg-gray-800 rounded-xl p-6">
                <h2 className="text-xl font-bold mb-4 text-purple-400">ðŸ“… Atividade Mensal</h2>
                <div className="h-64 bg-gray-900 rounded-lg flex items-end justify-between p-4">
                    {[30, 45, 60, 80, 100, 85, 70].map((value, index) => (
                        <div
                            key={index}
                            className="bg-gradient-to-t from-red-600 to-red-900 w-8 rounded-t-sm"
                            style={{ height: `${value}%` }}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
}

// Componente auxiliar para mÃ©tricas
const MetricCard = ({ title, value, icon }: { title: string, value: string, icon: string }) => (
    <div className="bg-gray-800 rounded-xl p-6 border border-gray-700">
        <div className="text-gray-400 mb-2">{title}</div>
        <div className="text-3xl font-bold">
            {icon} {value}
        </div>
    </div>
);
