import { useState, useEffect } from 'react';

const jogadores = [
    {
        nome: "üî´ FalleN",
        titulo: "O PAI T√Å ON",
        descricao: "Voc√™ posta thread motivacional na segunda, mas t√° jogando Faceit desde o domingo √† noite...",
        imagem: "/images/fallen.png",
        cor: "text-yellow-400",
        efeito: "hover:shadow-yellow-400/50"
    },
    {
        nome: "üòà arT",
        titulo: "LOUCURA CONTROLADA",
        descricao: "Voc√™ clicou antes de pensar. Teu coach fala 'slow round', voc√™ j√° puxou a AWP e t√° rusheando...",
        imagem: "/images/art.png",
        cor: "text-red-500",
        efeito: "hover:shadow-red-500/50"
    }
];

export default function DescubraPage() {
    const [fase, setFase] = useState<'inicio' | 'loading' | 'resultado'>('inicio');
    const [jogador, setJogador] = useState<any>(null);
    const [nomeAtual, setNomeAtual] = useState('');

    useEffect(() => {
        if (fase === 'loading') {
            const nomes = ["FalleN", "arT", "KSCERATO", "chelo", "guerri"];
            const interval = setInterval(() => {
                setNomeAtual(nomes[Math.floor(Math.random() * nomes.length)]);
            }, 150);

            setTimeout(() => {
                clearInterval(interval);
                setJogador(jogadores[Math.floor(Math.random() * jogadores.length)]);
                setFase('resultado');
            }, 2500);

            return () => clearInterval(interval);
        }
    }, [fase]);

    return (
        <div className="min-h-screen bg-gray-900 text-white flex items-center justify-center p-4 relative overflow-hidden">
            {/* Efeito de part√≠culas */}
            <div className="absolute inset-0 overflow-hidden">
                {[...Array(20)].map((_, i) => (
                    <div
                        key={i}
                        className="absolute bg-white rounded-full animate-float"
                        style={{
                            width: `${Math.random() * 4 + 1}px`,
                            height: `${Math.random() * 4 + 1}px`,
                            left: `${Math.random() * 100}%`,
                            top: `${Math.random() * 100}%`,
                            animationDuration: `${Math.random() * 20 + 10}s`,
                            opacity: Math.random() * 0.3 + 0.1
                        }}
                    />
                ))}
            </div>

            {/* Efeito de grade gamer */}
            <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI0MCIgaGVpZ2h0PSI0MCI+PHBhdGggZD0iTTAgMEg0MFY0MEgweiIgZmlsbD0ibm9uZSIgc3Ryb2tlPSIjMzMzIi8+PC9zdmc+')] opacity-10" />

            {/* Conte√∫do principal */}
            <div className="relative z-10 w-full max-w-2xl">
                {fase === 'inicio' && (
                    <div className="text-center backdrop-blur-sm bg-gray-900/80 p-8 rounded-xl border border-gray-800 shadow-2xl">
                        <img
                            src="/images/furia-logo.png"
                            alt="FURIA Esports"
                            className="w-40 mx-auto mb-8 transform hover:scale-110 transition duration-500"
                        />
                        <h1 className="text-5xl font-bold mb-6 text-transparent bg-clip-text bg-gradient-to-r from-red-500 to-yellow-400 font-mono">
                            QUAL FURIAZ√ÉO √â VOC√ä?
                        </h1>
                        <p className="text-xl text-gray-300 mb-8 tracking-wider">
                            DESCUBRA SEU ESTILO DE JOGO COM A INTELIG√äNCIA DAS M√ÅQUINAS
                        </p>
                        <button
                            onClick={() => setFase('loading')}
                            className="relative overflow-hidden bg-gradient-to-r from-red-600 to-red-800 text-white px-12 py-4 rounded-full font-bold text-lg tracking-wider hover:from-red-700 hover:to-red-900 transition-all duration-300 shadow-lg hover:shadow-red-500/40 border-2 border-red-900 group"
                        >
                            <span className="relative z-10">INICIAR SIMULA√á√ÉO</span>
                            <span className="absolute inset-0 bg-gradient-to-r from-yellow-400 to-red-500 opacity-0 group-hover:opacity-20 transition-opacity duration-500"></span>
                        </button>
                    </div>
                )}

                {fase === 'loading' && (
                    <div className="text-center backdrop-blur-sm bg-gray-900/90 p-10 rounded-xl border border-gray-700 shadow-xl">
                        <div className="animate-pulse mb-10">
                            <img
                                src="/images/furia-logo.png"
                                alt="FURIA Esports"
                                className="w-32 mx-auto opacity-90 filter drop-shadow-lg"
                            />
                        </div>
                        <h2 className="text-3xl font-bold mb-8 text-transparent bg-clip-text bg-gradient-to-r from-red-400 to-red-600 font-mono tracking-wider">
                            ANALISANDO SEU PLAYSTYLE...
                        </h2>
                        <div className="mb-10">
                            <p className="text-4xl font-bold text-white mb-6 animate-pulse font-mono tracking-wider">
                                {nomeAtual}
                            </p>
                            <div className="w-full h-2 bg-gray-800 rounded-full overflow-hidden shadow-inner">
                                <div
                                    className="h-full bg-gradient-to-r from-red-500 to-yellow-400 rounded-full animate-progress"
                                    style={{ animationDuration: '2.5s' }}
                                />
                            </div>
                        </div>
                        <p className="text-sm text-gray-400 tracking-wider">
                            PROCESSANDO 87.431 DATAPOINTS DE PERFORMANCE...
                        </p>
                    </div>
                )}

                {fase === 'resultado' && jogador && (
                    <div className="text-center backdrop-blur-sm bg-gray-900/90 p-8 rounded-xl border border-gray-700 shadow-2xl">
                        <img
                            src="/images/furia-logo.png"
                            alt="FURIA Esports"
                            className="w-24 mx-auto mb-6 opacity-90"
                        />

                        <div className={`relative mb-10 mx-auto w-56 h-56 ${jogador.efeito} transition-all duration-500`}>
                            <div className={`absolute inset-0 rounded-full border-4 ${jogador.cor.replace('text', 'border')} opacity-30 animate-spin-slow`}></div>
                            <div className="absolute inset-0 rounded-full bg-black/30"></div>
                            <img
                                src={jogador.imagem}
                                alt={jogador.nome}
                                className="w-full h-full object-cover rounded-full border-4 border-white/80 relative z-10 transform hover:scale-105 transition duration-500"
                            />
                        </div>

                        <div className="mb-8">
                            <h2 className={`text-5xl font-bold mb-3 ${jogador.cor} font-mono tracking-wider`}>
                                {jogador.nome}
                            </h2>
                            <h3 className="text-2xl font-semibold text-gray-300 mb-6 tracking-wider">
                                {jogador.titulo}
                            </h3>
                            <div className="bg-black/40 p-6 rounded-lg border border-gray-800 text-left">
                                <p className="text-gray-200 leading-relaxed tracking-wide">
                                    {jogador.descricao}
                                </p>
                            </div>
                        </div>

                        <button
                            onClick={() => setFase('inicio')}
                            className="bg-gradient-to-r from-gray-700 to-gray-900 hover:from-gray-600 hover:to-gray-800 text-white px-8 py-3 rounded-full font-bold tracking-wider border border-gray-700 hover:border-gray-600 transition-all duration-300 shadow-lg"
                        >
                            REPETIR AN√ÅLISE
                        </button>
                    </div>
                )}
            </div>

            {/* Efeitos adicionais */}
            <div className="absolute bottom-4 right-4 text-xs text-gray-500">
                SISTEMA DE AN√ÅLISE POR IA - v2.4.1
            </div>
        </div>
    );
}
