// DashboardGamer.tsx
import React from 'react';

export default function Dashboard() {
    // Dados fictÃ­cios MEGA GAMER
    const gamerData = {
        engagement: {
            peakHours: {
                labels: ['18h', '19h', '20h', '21h', '22h'],
                data: [65, 92, 88, 76, 63]
            },
            contentPerformance: [
                { type: 'Clutch Moments', engagement: 95 },
                { type: 'Player Cams', engagement: 82 },
                { type: 'Trash Talk', engagement: 78 }
            ]
        },
        playerImpact: {
            fallen: { matches: 24, impact: 92 },
            art: { matches: 24, impact: 88 },
            kscerato: { matches: 22, impact: 85 }
        },
        revenueTriggers: {
            merch: { mentions: 128, trend: 'â†‘23%' },
            tickets: { mentions: 56, trend: 'â†‘12%' },
            partners: { clicks: 42, trend: 'â†‘8%' }
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white p-0 overflow-hidden font-mono">
            {/* Header Estilo Gamer */}
            <div className="bg-black border-b border-red-500/50 p-4 flex justify-between items-center">
                <div className="flex items-center">
                    <img
                        src="/images/furia-logo.png"
                        className="h-10 mr-3 glow-red"
                        alt="FURIA"
                    />
                    <h1 className="text-2xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-red-500 to-yellow-400 tracking-tighter">
                        FURIA COMMAND CENTER
                    </h1>
                </div>
                <div className="text-sm bg-gray-800 px-3 py-1 rounded-full border border-gray-600 text-green-400">
                    LIVE DATA SYNCED
                </div>
            </div>

            {/* Grid Principal */}
            <div className="grid grid-cols-1 lg:grid-cols-3 gap-1 p-1 bg-gray-900/50">
                {/* COLUNA 1 - ENGAGEMENT */}
                <div className="bg-gray-900/90 border border-gray-800 rounded-lg p-4">
                    <PanelTitle icon="ðŸ”¥" title="ENGAGEMENT HEATMAP" />

                    <div className="mt-6 space-y-6">
                        <div>
                            <h3 className="text-yellow-400 mb-2">TOP PLAY TIMES</h3>
                            <div className="flex h-48 items-end gap-1">
                                {gamerData.engagement.peakHours.data.map((value, idx) => (
                                    <div
                                        key={idx}
                                        className="flex-1 bg-gradient-to-t from-red-900 to-red-500 rounded-t-sm relative group"
                                        style={{ height: `${value}%` }}
                                    >
                                        <div className="absolute -top-8 left-0 right-0 text-center text-xs text-gray-400">
                                            {gamerData.engagement.peakHours.labels[idx]}
                                        </div>
                                        <div className="absolute -bottom-6 left-0 right-0 text-center text-sm font-bold text-red-400">
                                            {value}%
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>

                        <div>
                            <h3 className="text-purple-400 mb-2">CONTENT RANKING</h3>
                            <div className="space-y-2">
                                {gamerData.engagement.contentPerformance.map((item, idx) => (
                                    <div key={idx} className="flex items-center">
                                        <div className={`w-8 h-8 rounded-sm ${
                                            idx === 0 ? 'bg-yellow-500/80' :
                                                idx === 1 ? 'bg-red-500/80' : 'bg-gray-700'
                                        } flex items-center justify-center mr-2 text-xs font-bold`}>
                                            #{idx + 1}
                                        </div>
                                        <div className="flex-1">
                                            <div className="flex justify-between">
                                                <span>{item.type}</span>
                                                <span className="text-green-400">{item.engagement}%</span>
                                            </div>
                                            <div className="w-full bg-gray-800 h-1 mt-1">
                                                <div
                                                    className="bg-gradient-to-r from-purple-500 to-red-500 h-1"
                                                    style={{ width: `${item.engagement}%` }}
                                                />
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>
                </div>

                {/* COLUNA 2 - PLAYER IMPACT */}
                <div className="bg-gray-900/90 border border-gray-800 rounded-lg p-4">
                    <PanelTitle icon="âš”ï¸" title="PLAYER IMPACT" />

                    <div className="mt-6 space-y-6">
                        {Object.entries(gamerData.playerImpact).map(([player, data]) => (
                            <div key={player} className="bg-gray-800/50 p-3 rounded border border-gray-700">
                                <div className="flex justify-between items-start">
                                    <div>
                                        <div className="text-xl font-bold text-yellow-400">
                                            {player.toUpperCase()}
                                        </div>
                                        <div className="text-sm text-gray-400">
                                            {data.matches} matches analyzed
                                        </div>
                                    </div>
                                    <div className="text-3xl font-bold text-green-400">
                                        {data.impact}%
                                    </div>
                                </div>
                                <div className="mt-2 w-full bg-gray-700 h-2 rounded-full overflow-hidden">
                                    <div
                                        className="bg-gradient-to-r from-yellow-600 to-red-500 h-2"
                                        style={{ width: `${data.impact}%` }}
                                    />
                                </div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* COLUNA 3 - REVENUE STREAM */}
                <div className="bg-gray-900/90 border border-gray-800 rounded-lg p-4">
                    <PanelTitle icon="ðŸ’°" title="REVENUE STREAM" />

                    <div className="mt-6 space-y-6">
                        <MetricCard
                            title="MERCH MENTIONS"
                            value={gamerData.revenueTriggers.merch.mentions}
                            trend={gamerData.revenueTriggers.merch.trend}
                            color="text-purple-400"
                            icon="ðŸ‘•"
                        />

                        <MetricCard
                            title="EVENT INTEREST"
                            value={gamerData.revenueTriggers.tickets.mentions}
                            trend={gamerData.revenueTriggers.tickets.trend}
                            color="text-yellow-400"
                            icon="ðŸŽŸï¸"
                        />

                        <MetricCard
                            title="PARTNER CLICKS"
                            value={gamerData.revenueTriggers.partners.clicks}
