// Dashboard.tsx - Versão Estratégica
import React from 'react';

export default function Dashboard() {
    // Dados fictícios baseados em análise de Twitter/X
    const marketingData = {
        engagement: {
            peakHours: [
                { hour: '18h', activity: 78 },
                { hour: '19h', activity: 95 },
                { hour: '20h', activity: 88 },
                { hour: '21h', activity: 72 }
            ],
            bestContent: ['Highlights', 'Behind the Scenes', 'Player Takeovers']
        },
        audience: {
            totalFans: 12458,
            topCountries: ['Brazil', 'USA', 'Germany'],
            devices: { mobile: 68, desktop: 32 }
        },
        sentiment: {
            positive: 84,
            neutral: 12,
            negative: 4
        },
        revenueDrivers: {
            merchMentions: 142,
            eventInterest: 89,
            partnerClicks: 56
        }
    };

    return (
        <div className="min-h-screen bg-gray-900 text-white p-6">
            {/* Header */}
            <div className="flex items-center justify-between mb-8 border-b border-gray-700 pb-4">
                <div className="flex items-center">
                    <img src="/images/furia-logo.png" className="h-10 mr-3" alt="FURIA" />
                    <h1 className="text-2xl font-bold bg-gradient-to-r from-red-500 to-yellow-400 bg-clip-text text-transparent">
                        FURIA Marketing Intelligence
                    </h1>
                </div>
                <div className="text-sm bg-gray-800 px-3 py-1 rounded-full">
                    Atualizado: {new Date().toLocaleDateString()}
                </div>
            </div>

            {/* Métricas Principais */}
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
                <MetricCard
                    title="Fãs Ativos"
                    value={marketingData.audience.totalFans.toLocaleString()}
                    trend="+12%"
                    icon="👥"
                />
                <MetricCard
                    title="Horário de Pico"
                    value="19h"
                    trend="Engajamento 95%"
                    icon="⏰"
                />
                <MetricCard
                    title="Sentimento Positivo"
                    value={`${marketingData.sentiment.positive}%`}
                    trend="+5%"
                    icon="😊"
                />
                <MetricCard
                    title="Menções a Merch"
                    value={marketingData.revenueDrivers.merchMentions}
                    trend="+23%"
                    icon="🛍️"
                />
            </div>

            {/* Seção de Engajamento */}
            <Section title="Estratégia de Conteúdo">
                <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
                    <div className="col-span-2">
                        <h3 className="text-lg font-semibold mb-3 text-yellow-400">📈 Horários de Pico</h3>
                        <div className="bg-gray-800 rounded-lg p-4 h-64">
                            <div className="flex items-end h-48 gap-2">
                                {marketingData.engagement.peakHours.map((item, index) => (
                                    <div key={index} className="flex-1 flex flex-col items-center">
                                        <div
                                            className="w-full bg-gradient-to-t from-red-600 to-red-900 rounded-t-sm"
                                            style={{ height: `${item.activity}%` }}
                                        />
                                        <div className="text-xs mt-2 text-gray-400">{item.hour}</div>
                                    </div>
                                ))}
                            </div>
                        </div>
                    </div>

                    <div>
                        <h3 className="text-lg font-semibold mb-3 text-red-400">🎯 Conteúdo que Engaja</h3>
                        <div className="bg-gray-800 rounded-lg p-4 h-64">
                            <ul className="space-y-3">
                                {marketingData.engagement.bestContent.map((item, index) => (
                                    <li key={index} className="flex items-center">
                                        <div className="w-8 h-8 rounded-full bg-red-900/50 flex items-center justify-center mr-3">
                                            {index + 1}
                                        </div>
                                        <span>{item}</span>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    </div>
                </div>
            </Section>

            {/* Seção de Receita */}
            <Section title="Oportunidades de Receita">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    <RevenueCard
                        title="Interesse em Merchandising"
                        value={marketingData.revenueDrivers.merchMentions}
                        action="Lançar coleção limitada"
                    />
                    <RevenueCard
                        title="Eventos Presenciais"
                        value={marketingData.revenueDrivers.eventInterest}
                        action="Promover meet & greet"
                    />
                    <RevenueCard
                        title="Cliques em Patrocinadores"
                        value={marketingData.revenueDrivers.partnerClicks}
                        action="Otimizar anúncios"
                    />
                </div>
            </Section>
        </div>
    );
}

// Componentes auxiliares
const MetricCard = ({ title, value, trend, icon }: any) => (
    <div className="bg-gray-800/50 border border-gray-700 rounded-lg p-4">
        <div className="text-gray-400 mb-1">{title}</div>
        <div className="flex items-end justify-between">
            <div className="text-2xl font-bold">
                {icon} {value}
            </div>
            <div className="text-sm text-green-400">{trend}</div>
        </div>
    </div>
);

const Section = ({ title, children }: any) => (
    <div className="mb-8">
        <h2 className="text-xl font-bold mb-4 text-white border-b border-gray-800 pb-2">
            {title}
        </h2>
        {children}
    </div>
);

const RevenueCard = ({ title, value, action }: any) => (
    <div className="bg-gray-800/50 border border-gray-700 rounded-lg p-4 hover:border-red-500 transition">
        <div className="text-gray-400 mb-2">{title}</div>
        <div className="text-3xl font-bold mb-4 text-yellow-400">{value}</div>
        <div className="text-sm bg-red-900/20 text-red-400 inline-block px-3 py-1 rounded-full">
            {action}
        </div>
    </div>
);
